-- Create "email_verifications" table
CREATE TABLE `email_verifications` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NOT NULL, `update_time` timestamp NOT NULL, `email` varchar(255) NOT NULL, `token` varchar(255) NOT NULL, `expired_at` timestamp NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `email` (`email`), INDEX `emailverification_email` (`email`), INDEX `emailverification_expired_at` (`expired_at`), INDEX `emailverification_token` (`token`), UNIQUE INDEX `token` (`token`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "lesson_reservations" table
CREATE TABLE `lesson_reservations` (`id` bigint NOT NULL AUTO_INCREMENT, `lesson_schedule_id` bigint NOT NULL, `school_id` bigint NOT NULL, `user_id` bigint NOT NULL, `reservation_status` enum('PENDING','APPROVED','CANCELED') NOT NULL, `count_student` varchar(255) NOT NULL, `graduate` varchar(255) NOT NULL, `subject` varchar(255) NOT NULL, `remarks` varchar(255) NULL, `reservation_confirm_at` timestamp NULL, `deleted_at` timestamp NULL, `updated_at` timestamp NOT NULL, `created_at` timestamp NOT NULL, PRIMARY KEY (`id`), INDEX `lessonreservation_created_at` (`created_at`), INDEX `lessonreservation_lesson_schedule_id` (`lesson_schedule_id`), INDEX `lessonreservation_reservation_status` (`reservation_status`), INDEX `lessonreservation_school_id` (`school_id`), INDEX `lessonreservation_user_id` (`user_id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "upload_files" table
CREATE TABLE `upload_files` (`id` bigint NOT NULL AUTO_INCREMENT, `create_time` timestamp NOT NULL, `update_time` timestamp NOT NULL, `photo_key` varchar(255) NOT NULL, `user_id` bigint NOT NULL, PRIMARY KEY (`id`), INDEX `uploadfile_photo_key` (`photo_key`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
